var app=angular.module("flapperNews",["ui.router","ngMaterial"]);app.config(["$stateProvider","$urlRouterProvider",function(t,e){t.state("home",{url:"/home",templateUrl:"/templates/home.html",controller:"MainCtrl",resolve:{postPromise:["posts",function(t){return t.getAll()}]}}).state("posts",{url:"/posts/{id}",templateUrl:"/templates/posts.html",controller:"PostsCtrl",resolve:{post:["$stateParams","posts",function(t,e){return e.get(t.id)}]}}).state("login",{url:"/login",templateUrl:"/templates/login.html",controller:"AuthCtrl",onEnter:["$state","auth",function(t,e){e.isLoggedIn()&&t.go("home")}]}).state("register",{url:"/register",templateUrl:"/templates/register.html",controller:"AuthCtrl",onEnter:["$state","auth",function(t,e){e.isLoggedIn()&&t.go("home")}]}).state("images",{url:"/images",templateUrl:"/templates/slider.html",controller:"SliderCtrl"}),e.otherwise("home")}]);